import { useState } from 'react';
import axios from 'axios';
import '../css/loginForm.css';

const projectID = '908c0447-262d-4bd2-bfa1-3d9d4b4aa5a8';

const Modal = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const authObject = { 'Project-ID': projectID, 'User-Name': username, 'User-Secret': password };

    try {
      await axios.get('https://api.chatengine.io/chats', { headers: authObject });

      localStorage.setItem('username', username);
      localStorage.setItem('password', password);

      window.location.reload();
      setError('');
    } catch (err) {
      setError('Oops, incorrect credentials.');
    }
  };

  return (
    // <div className="wrapper">
    //   <div className="form">
    //     <h1 className="title">Chat Application</h1>
    //     <form onSubmit={handleSubmit}>
    //       <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} className="input" placeholder="Username" required />
    //       <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} className="input" placeholder="Password" required />
    //       <div align="center">
    //         <button type="submit" className="button">
    //           <span>Start chatting</span>
    //         </button>
    //       </div>
    //     </form>
    //     <h1>{error}</h1>
    //   </div>
    // </div>
    <div className="login_signup_body">
      <div className="box" id="centered-form">
        <form className="loginForm" onSubmit={handleSubmit}>
          <span className="first_login_header">Welcome back!</span>
          <span className="second_login_header">Login to your account</span>
          <div className="input-container">
            <input className="form-control" type="text" value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Username" required />
            <label>Email</label>
          </div>
          <div className="input-container">
            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} className="form-control" placeholder="Password" required />
            <label>Password</label>
          </div>
          <div className="form-field">
            <button className="center__me button-82-pushable" type="submit" value="Login">
              <span className="button-82-shadow" />
              <span className="button-82-edge" />
              <span className="button-82-front text">
                Login now
              </span>
            </button>
            <h1>{error}</h1>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Modal;
